// src/msalConfig.js
import { PublicClientApplication } from '@azure/msal-browser';

export const msalConfig = {
  auth: {
    clientId: 'YOUR_CLIENT_ID',  // Reemplaza con tu Client ID desde Azure AD
    authority: 'https://login.microsoftonline.com/YOUR_TENANT_ID',  // Reemplaza con tu Tenant ID
    redirectUri: `${window.location.origin}/auth/callback`,  // Asegúrate de que esta URI sea correcta
  },
  cache: {
    cacheLocation: 'sessionStorage',
    storeAuthStateInCookie: true,  // Para compatibilidad con navegadores antiguos
  },
};

export const loginRequest = {
  scopes: ['User.Read', 'openid', 'profile'],
};

export const msalInstance = new PublicClientApplication(msalConfig);
