import React, { useState, useEffect } from 'react';
import { useMsal } from '@azure/msal-react';
import { msalInstance, loginRequest } from './msalConfig';  // Configuración de MSAL
import TaskForm from './components/TaskForm';
import TaskList from './components/TaskList';
import { useNavigate } from 'react-router-dom';  // Para redireccionar

const App = () => {
  const { instance, accounts } = useMsal();  // Obtener la instancia de MSAL y los detalles de la cuenta
  const [tasks, setTasks] = useState([]);
  const [accessToken, setAccessToken] = useState('');
  const navigate = useNavigate();

  const addTask = (newTask) => {
    setTasks([...tasks, newTask]);
  };

  const handleLogin = async () => {
    try {
      // Realizar login con Popup
      const loginResponse = await instance.loginPopup(loginRequest);
      console.log('Login successful:', loginResponse);

      // Guardar el access token para autenticar las peticiones al backend
      setAccessToken(loginResponse.accessToken);
    } catch (error) {
      console.error('Login failed:', error);
    }
  };

  const handleLogout = () => {
    instance.logoutRedirect();
    setAccessToken('');  // Limpiar el token al cerrar sesión
  };

  useEffect(() => {
    if (accounts.length > 0) {
      // Si la cuenta está disponible, asignar el idToken para mostrar el nombre de usuario
      console.log('User info:', accounts[0]);
    }
  }, [accounts]);

  return (
    <div>
      <h1>Task Manager</h1>
      {!accounts.length ? (
        <button onClick={handleLogin}>Sign in with Azure AD</button>
      ) : (
        <>
          <p>Welcome, {accounts[0].username}</p>
          <button onClick={handleLogout}>Sign out</button>
          <TaskForm addTask={addTask} accessToken={accessToken} />
          <TaskList tasks={tasks} />
        </>
      )}
    </div>
  );
};

export default App;
