// src/msalConfig.js
import { PublicClientApplication } from '@azure/msal-browser'; // Asegúrate de que esta importación sea correcta

export const msalConfig = {
  auth: {
    clientId: 'YOUR_CLIENT_ID',  // Reemplaza con tu Client ID
    authority: 'https://login.microsoftonline.com/YOUR_TENANT_ID',  // Reemplaza con tu Tenant ID
    redirectUri: `${window.location.origin}/auth/callback`,  // La URI de redirección
  },
  cache: {
    cacheLocation: 'sessionStorage',
    storeAuthStateInCookie: true,  // Para compatibilidad con navegadores antiguos
  },
};

export const msalInstance = new PublicClientApplication(msalConfig);
