// src/msalConfig.js
import { PublicClientApplication } from '@azure/msal-browser';

export const msalConfig = {
  auth: {
    clientId: 'e0d5238b-6f6a-4a36-badb-671f593255b0', // Reemplaza con tu Client ID desde Azure AD
    authority: 'https://login.microsoftonline.com/a8bb09eb-8699-47bf-ba6b-122458d196e7', // Reemplaza con tu Tenant ID
    redirectUri: 'http://localhost:3000/auth/callback', // URI de redirección
  },
  cache: {
    cacheLocation: 'sessionStorage', // O 'localStorage'
    storeAuthStateInCookie: true, // Para compatibilidad con navegadores antiguos
  },
};

export const loginRequest = {
  scopes: ['User.Read', 'openid', 'profile'],
};

export const msalInstance = new PublicClientApplication(msalConfig);
